+      >
+        <Drawer.Screen
+          options={{
+            drawerIcon: () => <AntDesign name="home" size={24} color="black" />,
+          }}
+          component={HomeScreen}
+          name="HomeScreen"
+        />
+        <Drawer.Screen
+          options={{
+            drawerIcon: () => (
+              <Ionicons name="briefcase-outline" size={24} color="black" />
+            ),
+          }}
+          component={AboutStack}
+          name="Илова ҳақида"
+        />
+        <Drawer.Screen
+          options={{
+            drawerIcon: () => (
+              <Ionicons name="ios-language-outline" size={24} color="black" />
+            ),
+          }}
+          component={LanguageStack}
+          name="Тил"
+        />
+        <Drawer.Screen
+          options={{
+            drawerIcon: () => (
+              <SimpleLineIcons name="trophy" size={24} color="black" />
+            ),
+          }}
+          component={PromoActsiyaStack}
+          name="Промо-акция"
+        />
+        <Drawer.Screen
+          options={{
+            drawerIcon: () => (
+              <AntDesign name="filetext1" size={24} color="black" />
+            ),
+          }}
+          component={OffertaStack}
+          name="Оммавий офферта"
+        />
+        <Drawer.Screen
+          options={{
+            drawerIcon: () => (
+              <MaterialIcons name="history" size={24} color="black" />
+            ),
+          }}
+          component={HistoryStack}
+          name="Қидирувлар тарихи"
+        />
+        <Drawer.Screen
+          options={{
+            drawerIcon: () => <Feather name="phone" size={24} color="black" />,
+          }}
+          component={ContactStack}
+          name="+998 71 203 6766"
+        />
+      </Drawer.Navigator>
+    </NavigationContainer>
+  );
+}
+
+export default App;

