+import React, { useEffect } from "react";
+import { Linking, StyleSheet } from "react-native";
+import { Constants } from "expo";
+import { createAppContainer } from "react-navigation";
+import {
+  createDrawerNavigator,
+  DrawerItemList,
+} from "@react-navigation/drawer";
+import { createStackNavigator } from "@react-navigation/stack";
+
+import { HomeScreen } from "./screens";
+import { NavigationContainer } from "@react-navigation/native";
+import {
+  Entypo,
+  AntDesign,
+  Ionicons,
+  SimpleLineIcons,
+  Feather,
+  MaterialIcons,
+} from "@expo/vector-icons";
+import { TouchableOpacity } from "react-native-gesture-handler";
+
+import AboutScreen from "./components/Pages/AboutScreen";
+import LanguageScreen from "./components/Pages/LanguageScreen";
+import PromoActsiyaScreen from "./components/Pages/PromoActsiya";
+import OmmaviyOffertaScreen from "./components/Pages/OmmaviyOffertaScreen";
+import HistoryOfSearchScreen from "./components/Pages/HistoryOfSearchScreen";
+import { Text } from "react-native";
+
+const Drawer = createDrawerNavigator();
+
+const Stack = createStackNavigator();
+
+function AboutStack({ navigation }) {
+  return (
+    <Stack.Navigator>
+      <Stack.Screen
+        name="About"
+        component={AboutScreen}
+        navigation={navigation}
+        options={{
+          headerLeft: () => (
+            <TouchableOpacity onPress={() => navigation.navigate("HomeScreen")}>
+              <Entypo name="chevron-small-left" size={30} color="black" />
+            </TouchableOpacity>
+          ),
+        }}
+      />
+    </Stack.Navigator>
+  );
+}
+
+function LanguageStack({ navigation }) {
+  return (
+    <Stack.Navigator>
+      <Stack.Screen
+        name="Til"
+        component={LanguageScreen}
+        navigation={navigation}
+        options={{
+          headerLeft: () => (
+            <TouchableOpacity onPress={() => navigation.navigate("HomeScreen")}>
+              <Entypo name="chevron-small-left" size={30} color="black" />
+            </TouchableOpacity>
+          ),
+        }}
+      />
+    </Stack.Navigator>
+  );
+}
+
+function PromoActsiyaStack({ navigation }) {
+  return (
+    <Stack.Navigator>
+      <Stack.Screen
+        name="Promo Actsiya"
+        component={PromoActsiyaScreen}
+        navigation={navigation}
+        options={{
+          headerLeft: () => (
+            <TouchableOpacity onPress={() => navigation.navigate("HomeScreen")}>
+              <Entypo name="chevron-small-left" size={30} color="black" />
+            </TouchableOpacity>
+          ),
+        }}
+      />
+    </Stack.Navigator>
+  );
+}
+
+function OffertaStack({ navigation }) {
+  return (
+    <Stack.Navigator>
+      <Stack.Screen
+        name="Ommaviy Offerta"
+        component={OmmaviyOffertaScreen}
+        navigation={navigation}
+        options={{
+          headerLeft: () => (
+            <TouchableOpacity onPress={() => navigation.navigate("HomeScreen")}>
+              <Entypo name="chevron-small-left" size={30} color="black" />
+            </TouchableOpacity>
+          ),
+        }}
+      />
+    </Stack.Navigator>
+  );
+}
+
+function HistoryStack({ navigation }) {
+  return (
+    <Stack.Navigator>
+      <Stack.Screen
+        name="Qidiruvlar tarixi"
+        component={HistoryOfSearchScreen}
+        navigation={navigation}
+        options={{
+          headerLeft: () => (
+            <TouchableOpacity onPress={() => navigation.navigate("HomeScreen")}>
+              <Entypo name="chevron-small-left" size={30} color="black" />
+            </TouchableOpacity>
+          ),
+        }}
+      />
+    </Stack.Navigator>
+  );
+}
+
+const _pressCall = () => {
+  const url = "tel://+998 71 203 6766";
+  Linking.openURL(url);
+};
+
+function ContactStack({ navigation }) {
+  return (
+    <Stack.Navigator>
+      <Stack.Screen
+        name="COntact"
+        component={_pressCall}
+        navigation={navigation}
+        options={{
+          headerLeft: () => (
+            <TouchableOpacity onPress={() => navigation.navigate("Home")}>
+              <Entypo name="chevron-small-left" size={30} color="black" />
+            </TouchableOpacity>
+          ),
+        }}
+      />
+    </Stack.Navigator>
+  );
+}
+
+/////////AAAAAAAAAAAAAAAAAAAAAAAA
+function CustomDrawerContent(props) {
+  return (
+    <DrawerContentScrollView {...props}>
+      <Text>AAAAAAAAAAAAAAA</Text>
+    </DrawerContentScrollView>
+  );
+}
+
+function App() {
+  return (
+    <NavigationContainer>
+      <Drawer.Navigator
+        drawerContentOptions={{
+          labelStyle: {
+            color: "black",
+            fontSize: 15,
+            // paddingLeft: -10,
+            marginLeft: -10,
+          },
+          itemStyle: {
+            borderColor: "#98A5A9",
+            borderBottomWidth: 0.3,
+            paddingTop: 4,
+            paddingBottom: 7,
+          },
+        }}
+        drawerPosition="right"
+      >
+        <DrawerItemList {...props} />
+        <Drawer.Screen
+          options={{
+            drawerIcon: () => (
+              <AntDesign
+                style={styles.iconDrawer}
+                name="home"
+                size={22}
+                color="#98A5A9"
+              />
+            ),
+          }}
+          component={HomeScreen}
+          name="HomeScreen"
+        />
+        <Drawer.Screen
+          options={{
+            swipeEnabled: false,
+            drawerIcon: () => (
+              <Ionicons
+                style={styles.iconDrawer}
+                name="briefcase-outline"
+                size={22}
+                color="#98A5A9"
+              />
+            ),
+          }}
+          component={AboutStack}
+          name="Илова ҳақида"
+        />
+        <Drawer.Screen
+          options={{
+            swipeEnabled: false,
+
+            drawerIcon: () => (
+              <Ionicons
+                style={styles.iconDrawer}
+                name="ios-language-outline"
+                size={22}
+                color="#98A5A9"
+              />
+            ),
+          }}
+          component={LanguageStack}
+          name="Тил"
+        />
+        <Drawer.Screen
+          options={{
+            swipeEnabled: false,
+
+            drawerIcon: () => (
+              <SimpleLineIcons
+                style={styles.iconDrawer}
+                name="trophy"
+                size={22}
+                color="#98A5A9"
+              />
+            ),
+          }}
+          component={PromoActsiyaStack}
+          name="Промо-акция"
+        />
+        <Drawer.Screen
+          options={{
+            swipeEnabled: false,
+
+            drawerIcon: () => (
+              <AntDesign
+                style={styles.iconDrawer}
+                name="filetext1"
+                size={22}
+                color="#98A5A9"
+              />
+            ),
+          }}
+          component={OffertaStack}
+          name="Оммавий офферта"
+        />
+        <Drawer.Screen
+          options={{
+            swipeEnabled: false,
+
+            drawerIcon: () => (
+              <MaterialIcons
+                style={styles.iconDrawer}
+                name="history"
+                size={22}
+                color="#98A5A9"
+              />
+            ),
+          }}
+          component={HistoryStack}
+          name="Қидирувлар тарихи"
+        />
+        <Drawer.Screen
+          options={{
+            drawerIcon: () => (
+              <Feather
+                style={styles.iconDrawer}
+                name="phone"
+                size={22}
+                color="#98A5A9"
+              />
+            ),
+          }}
+          component={ContactStack}
+          name="+998 71 203 6766"
+        />
+      </Drawer.Navigator>
+    </NavigationContainer>
+  );
+}
+
+export default App;
+
+const styles = StyleSheet.create({
+  iconDrawer: {
+    paddingLeft: 10,
+  },
+});

