import React from "react";
import {
  View,
  Image,
  Text,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
} from "react-native";
import HamburgerIcon from "../assets/menu.png";
function Screen() {
  return (
    <View>
      <SafeAreaView style={{ flex: 1 }}>
        <TouchableOpacity
          style={{ alignItems: "flex-end", matgin: 16 }}
          onPress={navigation.openDrawer()}
        >
          <Image />
        </TouchableOpacity>
      </SafeAreaView>
    </View>
  );
}

export default Screen;
